import React from 'react';
import { useNavigate } from 'react-router-dom';
import background from './assets/sudokuBackground.jpg'; // Opdater stien til, hvor du gemte billedet

function MenuScreen() {
  let navigate = useNavigate();

  const handleStartGame = () => {
    navigate('/sudoku'); // Navigerer brugeren til SudokuView komponenten
  };

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      backgroundImage: `url(${background})`, // Tilføjer baggrundsbilledet
      backgroundSize: 'cover', // Sikrer at baggrunden dækker hele div'en
      backgroundPosition: 'center' // Centrerer baggrundsbilledet
    }}>
      <h1 style={{ marginBottom: '20px', fontSize: '60px', color: 'white' }}>Velkommen til Sudoku!</h1>
      <button onClick={handleStartGame} className='button-blue' style={{ padding: '10px 20px', fontSize: '20px', cursor: 'pointer' }}>
        Start Spil
      </button>
    </div>
  );
}

export default MenuScreen;
