import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function MenuScreen() {
  let navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/leaderboard')
      .then(response => response.json())
      .then(data => {
        setLeaderboard(data.leaderboard);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  const handleStartGame = (event) => {
    event.preventDefault();
    // Assuming username validation or other client-side preparations are done here
    navigate('/sudoku'); // Directly navigate to the game screen without a POST request
  };

  return (
    <div style={{
      display: 'flex',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'lightblue', // En lys baggrundsfarve
    }}>
      {/* Leaderboard sektion */}
      <div style={{
        marginRight: '50px', // Tilføjer lidt afstand mellem leaderboard og startspil sektionen
        padding: '20px',
        borderRight: '2px solid #ccc', // Subtil indramning
      }}>
        <h2>Leaderboard</h2>
        <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
          {leaderboard.map((entry, index) => (
            <li key={index}>{entry.username}: {entry.score}</li>
          ))}
        </ul>
      </div>

      {/* Spilstart sektion */}
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}>
        <h1 style={{ marginBottom: '20px', fontSize: '60px' }}>Velkommen til Sudoku!</h1>
        <form onSubmit={handleStartGame}>
        <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Indtast brugernavn"
            style={{
            padding: '10px 15px', // Giver mere plads inde i inputfeltet
            fontSize: '16px', // Gør teksten større for bedre læsbarhed
            borderRadius: '5px', // Afrunder hjørnerne for et mere moderne udseende
            border: '1px solid #ccc', // En mere subtil kant
            marginBottom: '20px', // Tilføjer lidt mere plads under inputfeltet
            width: '250px', // Fast bredde for et mere ensartet udseende
            outline: 'none', // Fjerner den blå outline ved fokus (kan tilpasses efter behov)
            }}
            required
        />

          <button type="submit"
            style={{
                padding: '10px 20px',
                fontSize: '20px',
                cursor: 'pointer',
                color: 'black', // Tekstfarven sættes til hvid
                backgroundColor: '#007bff', // En blå baggrundsfarve,
                border: 'none', // Fjerner standardkanten
                borderRadius: '5px', // Tilføjer afrundede hjørner
            }}>
            Start Spil
          </button>
        </form>
      </div>
    </div>
  );
}

export default MenuScreen;
