import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function MenuScreen() {
  let navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/leaderboard')
      .then(response => response.json())
      .then(data => {
        setLeaderboard(data.leaderboard);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  const handleStartGame = (event) => {
    event.preventDefault();
    fetch('http://localhost:3000/submit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username }),
    })
    .then(response => response.json())
    .then(() => {
      navigate('/sudoku');
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  return (
    <div style={{
      display: 'flex',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: 'lightblue', // En lys baggrundsfarve
    }}>
      {/* Leaderboard sektion */}
      <div style={{
        marginRight: '50px', // Tilføjer lidt afstand mellem leaderboard og startspil sektionen
        padding: '20px',
        borderRight: '2px solid #ccc', // Subtil indramning
      }}>
        <h2>Leaderboard</h2>
        <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
          {leaderboard.map((entry, index) => (
            <li key={index}>{entry.username}: {entry.score}</li>
          ))}
        </ul>
      </div>

      {/* Spilstart sektion */}
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}>
        <h1 style={{ marginBottom: '20px', fontSize: '60px' }}>Velkommen til Sudoku!</h1>
        <form onSubmit={handleStartGame}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Indtast brugernavn"
            style={{ padding: '10px', marginBottom: '10px' }}
            required
          />
          <button type="submit" style={{ padding: '10px 20px', fontSize: '20px', cursor: 'pointer' }}>
            Start Spil
          </button>
        </form>
      </div>
    </div>
  );
}

export default MenuScreen;
