class sudokuGen {
    constructor() {
      this.grid = [];
    }
  
    initializeGrid() {
      this.grid = Array.from({ length: 9 }, () => Array(9).fill(0));
    }
  
    isValid(num, row, col) {
      for (let i = 0; i < 9; i++) {
        if (this.grid[row][i] === num || this.grid[i][col] === num) {
          return false;
        }
      }
      const startRow = Math.floor(row / 3) * 3;
      const startCol = Math.floor(col / 3) * 3;
      for (let i = 0; i < 3; i++) {
        for (let j = 0; j < 3; j++) {
          if (this.grid[startRow + i][startCol + j] === num) {
            return false;
          }
        }
      }
      return true;
    }
  
    fillGrid(row = 0, col = 0) {
      if (row === 9) {
        return true;
      }
      if (col === 9) {
        return this.fillGrid(row + 1, 0);
      }
      for (let num = 1; num <= 9; num++) {
        if (this.isValid(num, row, col)) {
          this.grid[row][col] = num;
          if (this.fillGrid(row, col + 1)) {
            return true;
          }
          this.grid[row][col] = 0;
        }
      }
      return false;
    }
  
    generateGrid() {
      this.initializeGrid();
      if (this.fillGrid()) {
        return this.grid;
      }
      return [];
    }
  }
