import React, { useState, useEffect } from 'react';
import './SudokuView.css';

function SudokuView() {
  const [grid, setGrid] = useState([]);
  const [validity, setValidity] = useState(Array(9).fill().map(() => Array(9).fill(true)));


  useEffect(() => {
    // Hent et nyt Sudoku bræt fra backenden
    fetch('http://localhost:3000/generate')
      .then(response => response.json())
      .then(data => setGrid(data.board))
      .catch(error => console.error('Error fetching data: ', error));
  }, []);

  const handleInputChange = (event, i, j) => {
    const value = event.target.value;
    // check om input er et tal mellem 1 og 9
    if (value === '' || (/^\d+$/.test(value) && value >= 1 && value <= 9)) {
      const numValue = value === '' ? 0 : parseInt(value, 10); // konverter til tal

      // lav en ny kopi af grid og opdater værdien på den valgte celle
      const newGrid = grid.map((row, rowIndex) =>
        row.map((cell, cellIndex) =>
          rowIndex === i && cellIndex === j ? numValue : cell
        )
      );

      setGrid(newGrid);
    }
    //hvis input ikke er et tal mellem 1 og 9, så gør ingenting
  };

  const isValidSudoku = () => {
    let newValidity = Array(9).fill().map(() => Array(9).fill(true)); // Reset validity
    let isValid = true;

    // Check rows and columns
    for (let i = 0; i < 9; i++) {
      const rowSet = new Set();
      const colSet = new Set();

      for (let j = 0; j < 9; j++) {
        const rowVal = grid[i][j];
        const colVal = grid[j][i];

        if (rowVal) {
          if (rowSet.has(rowVal)) {
            isValid = false;
            newValidity[i][j] = false; // Mark row cell as invalid
          } else {
            rowSet.add(rowVal);
          }
        }

        if (colVal) {
          if (colSet.has(colVal)) {
            isValid = false;
            newValidity[j][i] = false; // Mark column cell as invalid
          } else {
            colSet.add(colVal);
          }
        }
      }
    }

    // Check 3x3 boxes
    for (let boxStartRow = 0; boxStartRow < 9; boxStartRow += 3) {
      for (let boxStartCol = 0; boxStartCol < 9; boxStartCol += 3) {
        const boxSet = new Set();

        for (let r = 0; r < 3; r++) {
          for (let c = 0; c < 3; c++) {
            const val = grid[boxStartRow + r][boxStartCol + c];
            if (val) {
              if (boxSet.has(val)) {
                isValid = false;
                // Mark box cell as invalid
                newValidity[boxStartRow + r][boxStartCol + c] = false;
              } else {
                boxSet.add(val);
              }
            }
          }
        }
      }
    }

    // Update the validity state
    setValidity(newValidity);
    return isValid;
  };
  // tjek om sudoku er valid
  const checkSudoku = () => {
    alert(isValidSudoku()); //vis alert med resultat
  };

  return (
    <div className="SudokuView">
      <table className="center">
        <tbody>
          {grid.map((row, i) => (
            <tr key={i}>
              {row.map((value, j) => (
                <td key={j} className={!validity[i][j] ? 'invalid' : ''}>
                  <input
                    type="text"
                    value={value === 0 ? '' : value}
                    onChange={(event) => handleInputChange(event, i, j)}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <button
        className="button-blue"
        onClick={checkSudoku}>

          Check Sudoku

        </button>

    </div>
  );
}

export default SudokuView;
