import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import background from './assets/sudokuBackground.jpg';

function MenuScreen() {
  let navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/leaderboard')
      .then(response => response.json())
      .then(data => {
        setLeaderboard(data.leaderboard);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []);

  const handleStartGame = (event) => {
    event.preventDefault();
    fetch('http://localhost:3000/submit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username }),
    })
    .then(response => response.json())
    .then(() => {
      navigate('/sudoku');
    })
    .catch((error) => {
      console.error('Error:', error);
    });
  };

  return (
    <div style={{
      display: 'flex',
      height: '100vh',
      backgroundImage: `url(${background})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat'
    }}>
      {/* Leaderboard sektion */}
      <div style={{
        flex: 1,
        padding: '20px',
        borderRight: '2px solid white', // Indramning til leaderboardet
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}>
        <h2 style={{ color: 'white' }}>Leaderboard</h2>
        <ul style={{ listStyle: 'none', paddingLeft: 0, color: 'white' }}>
          {leaderboard.map((entry, index) => (
            <li key={index}>{entry.username}: {entry.score}</li>
          ))}
        </ul>
      </div>

      {/* Spilstart sektion */}
      <div style={{
        flex: 2,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
      }}>
        <h1 style={{ marginBottom: '20px', fontSize: '60px', color: 'white' }}>Velkommen til Sudoku!</h1>
        <form onSubmit={handleStartGame}>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Indtast brugernavn"
            style={{ padding: '10px', marginBottom: '10px' }}
            required
          />
          <button type="submit" className='button-blue' style={{ padding: '10px 20px', fontSize: '20px', cursor: 'pointer', color: 'white' }}>
            Start Spil
          </button>
        </form>
      </div>
    </div>
  );
}

export default MenuScreen;
