export const fetchNewBoard = ({
  setGrid,
  setEditableCells,
  setUserEdits,
  setValidity,
  setIsDataLoaded,
  setTimer,
  setIsTimerActive
}) => {
  console.log('Fetching new board...');

  fetch('http://localhost:3000/generate')
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      if (!data.board || !Array.isArray(data.board) || data.board.length === 0) {
        console.error('Invalid or empty board data received:', data);
        throw new Error('Invalid or empty board data received');
      }

      console.log('Board fetched:', data.board);
      const boardSize = data.board.length;

      setGrid(data.board);

      const editable = data.board.map(row => row.map(value => value === 0));
      setEditableCells(editable);

      const userEditsArray = Array(boardSize).fill().map(() => Array(boardSize).fill(false));
      setUserEdits(userEditsArray);

      const validityArray = Array(boardSize).fill().map(() => Array(boardSize).fill(true));
      setValidity(validityArray);

      setIsDataLoaded(true);
      setTimer(0);
      setIsTimerActive(true);
    })
    .catch(error => {
      console.error('Error fetching data:', error);
      // Consider setting some error state here to inform the user
      setIsDataLoaded(false); // Indicate that data loading failed
    });
};
