import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import UserContext from './UserContext'; // Husk at tilpasse stien til din UserContext fil

function MenuScreen() {
  let navigate = useNavigate();
  const [username, setUsername] = useState(''); // Tilføj denne linje for at definere username state
  // const { setUsername } = useContext(UserContext); // Denne linje er unødvendig med den direkte state definition

  const handleStartGame = (event) => {
    event.preventDefault();
    // Her skal du sandsynligvis ikke sende brugernavn til backenden endnu, men i stedet sætte det i context eller state
    // fetch('http://localhost:3000/submit', { ... })

    console.log('Success:', username);
    navigate('/sudoku');
  };

  const handleLeaderBoard = () => {
    navigate('/leaderboard');
  };

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      backgroundColor: '#e6f7ff',
    }}>
      <h1 style={{ marginBottom: '20px', fontSize: '60px', color: 'black' }}>Velkommen til Sudoku!</h1>
      <form onSubmit={handleStartGame}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Indtast brugernavn"
          required
        />
        <button type="submit">
          Start Spil
        </button>
      </form>
      <button onClick={handleLeaderBoard}>
        Leaderboard
      </button>
    </div>
  );
}

export default MenuScreen;
