import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function MenuScreen() {
  let navigate = useNavigate();
  const [username, setUsername] = useState('');

  const handleStartGame = (event) => {
    event.preventDefault();
    fetch('http://localhost:3000/submit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username }),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
        navigate('/sudoku');
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleLeaderBoard = () => {
    navigate('/leaderboard');
  };

  return (
    <div style={{ 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      backgroundColor: '#e6f7ff', // Afslappet lyseblå farve
    }}>
      <h1 style={{ marginBottom: '20px', fontSize: '60px', color: 'white' }}>Velkommen til Sudoku!</h1>
      <form onSubmit={handleStartGame}>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Indtast brugernavn"
          style={{
            padding: '5px 5px',
            fontSize: '16px',
            borderRadius: '7px',
            border: '1px solid #ccc',
            marginBottom: '1px',
            width: '250px',
            outline: 'none',
          }}
          required
        />
        <button type="submit" className='button-blue' style={{ padding: '10px 20px', fontSize: '20px', cursor: 'pointer', color: 'white'}}>
          Start Spil
        </button>
      </form>
      <button onClick={handleLeaderBoard} className='button-blue' style={{ padding: '10px 20px', fontSize: '20px', cursor: 'pointer', color: 'white', marginTop: '20px'}}>
        Leaderboard
      </button>
    </div>
  );
}

export default MenuScreen;
